import { useState } from 'react'
const Button =(props)=><button onClick={props.handleClick}>{props.text}</button>
const Statistics = ({props})=>{
    return(
        <div>
            <p>good {props.good}<br/>
                neutral {props.neutral}<br/>
                bad {props.bad}<br/>
                all {props.good+props.bad+props.neutral}<br/>
                average {(props.good-props.bad)/(props.good+props.bad+props.neutral)}<br/>
                positive {((props.good/(props.good+props.neutral+props.bad))*100+'%')}</p>
        </div>
    )
}
const App = () => {
    // save clicks of each button to its own state
    const [Value,setValue] =useState(
        {
            good:0,
            neutral:0,
            bad:0,
        }
    )
    const goodClick =() =>setValue({...Value, good:Value.good+1,});
    const neutralClick =() => setValue({...Value, neutral:Value.neutral+1,});
    const badClick =() =>setValue({...Value, bad:Value.bad+1,});
    return <div>
        <h1>give feedback</h1>
        <Button handleClick={goodClick} text ="good"/>
        <Button handleClick={neutralClick} text="neutral"/>
        <Button handleClick={badClick} text="bad"/><br/>
        <h1>statistics</h1>
        {(Value.good+Value.neutral+Value.bad)?<Statistics props={Value}/>:
            (<h4>No feedback given</h4>)}

    </div>
}

export default App;
