import { useState } from 'react'
const Button =(props)=><button onClick={props.handleClick}>{props.text}</button>
const Statistics = (props)=>{
    return <div>
        <h1>give feedback</h1>
        <Button handleClick={goodClick} text ="good"/>
        <Button handleClick={neutralClick} text="neutral"/>
        <Button handleClick={badClick} text="bad"/>
        <h1>statistics</h1>
        <p>good {Value.good}<br/>
            neutral {Value.neutral}<br/>
            bad {Value.bad}<br/>
            all {Value.good+Value.bad+Value.neutral}<br/>
            average {(Value.good+Value.bad+Value.neutral)?(Value.good-Value.bad)/(Value.good+Value.bad+Value.neutral):('待记录')}<br/>
            positive {(Value.good+Value.neutral+Value.bad)?((Value.good+Value.neutral+Value.bad)*100+'%'):('待记录')}</p>

    </div>

}
const App = () => {
    // save clicks of each button to its own state
    const [Value,setValue] =useState(
        {
            good:0,
            neutral:0,
            bad:0,
        }
    )
    const goodClick =() =>setValue({...Value, good:Value.good+1,});
    const neutralClick =() => setValue({...Value, neutral:Value.neutral+1,});
    const badClick =() =>setValue({...Value, bad:Value.bad+1,});

}

export default App;
